import { useState, useEffect, useRef } from "react";

import Lap from "../Lap/Lap";

import { StopwatchProps } from "../../utils/types/StopwatchProps.types";

class Stopwatch extends Component<StopwatchProps> {
	incrementer: NodeJS.Timeout | null = null;

	constructor(props: StopwatchProps) {
		super(props);

		this.state = {
			secondsElapsed: props.initialSeconds,
			lastClearedIncrementer: null,
			laps: [],
		};

		this.handleStartClick = this.handleStartClick.bind(this);
		this.handleStopClick = this.handleStopClick.bind(this);
		this.handleResetClick = this.handleResetClick.bind(this);
		this.handleLabClick = this.handleLabClick.bind(this);
		this.handleDeleteClick = this.handleDeleteClick.bind(this);
	}

	handleStartClick() {
		this.incrementer = setInterval(
			() =>
				this.setState({
					secondsElapsed: this.state.secondsElapsed + 1,
				}),
			1000
		);
	}

	handleStopClick() {
		if (this.incrementer !== null) {
			clearInterval(this.incrementer);
		}

		this.setState({
			lastClearedIncrementer: this.incrementer,
		});
	}

	handleResetClick() {
		if (this.incrementer !== null) {
			clearInterval(this.incrementer);
		}

		this.setState({
			secondsElapsed: 0,
			laps: [],
		});
	}

	handleLabClick() {
		this.setState((prevState) => ({
			laps: prevState.laps.concat([prevState.secondsElapsed]),
		}));
	}

	handleDeleteClick(index: number) {
		return () => {
			this.setState((prevState) => ({
				laps: prevState.laps.filter((_, i) => i !== index),
			}));
		};
	}

	render() {
		const { secondsElapsed, lastClearedIncrementer } = this.state;

		return (
			<div className="stopwatch">
				<h1 className="stopwatch-timer">{formattedSeconds(secondsElapsed)}</h1>
				{secondsElapsed === 0 || this.incrementer === lastClearedIncrementer ? (
					<button
						type="button"
						className="start-btn"
						onClick={this.handleStartClick}
					>
						start
					</button>
				) : (
					<button
						type="button"
						className="stop-btn"
						onClick={this.handleStopClick}
					>
						stop
					</button>
				)}
				{secondsElapsed !== 0 && this.incrementer !== lastClearedIncrementer ? (
					<button type="button" onClick={this.handleLabClick}>
						lap
					</button>
				) : null}
				{secondsElapsed !== 0 && this.incrementer === lastClearedIncrementer ? (
					<button type="button" onClick={this.handleResetClick}>
						reset
					</button>
				) : null}
				<div className="stopwatch-laps">
					{this.state.laps &&
						this.state.laps.map((lap, i) => (
							<Lap
								index={i + 1}
								lap={lap}
								onDelete={this.handleDeleteClick(i)}
							/>
						))}
				</div>
			</div>
		);
	}
}

export default Stopwatch;
